package com.shoaa.task;

import androidx.appcompat.app.AppCompatActivity;
import androidx.databinding.DataBindingUtil;
import androidx.recyclerview.widget.LinearLayoutManager;

import android.content.Intent;
import android.os.Bundle;
import android.os.Parcelable;
import android.view.View;

import com.shoaa.task.databinding.ActivityMainBinding;

import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity {

    ActivityMainBinding binding;
    AdapterMonintor adapter;
    public List<ModelMonitor> modelMonitors;
    String img1= "";
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        binding = DataBindingUtil.setContentView(MainActivity.this,
                R.layout.activity_main);
        adapter = new AdapterMonintor(null);
        binding.recyOrders.setLayoutManager(new LinearLayoutManager(this));
        modelMonitors= new ArrayList<>();
        modelMonitors.add(new ModelMonitor("محمد علاء"
                ,img1
                ,"لا يوجد اي ملاحطات"
                ,"مشرف","13/10/2021"
                ,img1
                ,img1
                ,"محمد احمد محمد"
                ,"امبابة الجيزة"));
        if (modelMonitors == null) {
            //errorMessage if data coming is null;
            binding.tvOrderListEmpty.setVisibility(View.VISIBLE);
        } else {
            binding.progressBar.setVisibility(View.GONE);
            adapter = new AdapterMonintor(modelMonitors);
            binding.recyOrders.setAdapter(adapter);
            adapter.setOnItemClickListener(new AdapterMonintor.OnItemClickListener() {
                @Override
                public void onItemClick(int pos, ModelMonitor modelMonitor) {
                    Intent intent = new Intent(MainActivity.this, DetailsMonitorActivity.class);
                    intent.putExtra("img3", modelMonitor.getImg3());
                    intent.putExtra("img2", modelMonitor.getImg2());
                    intent.putExtra("img1", modelMonitor.getImg1());
                    intent.putExtra("name_ower", modelMonitor.getNameOwer());
                    intent.putExtra("notese", modelMonitor.getNotese());
                    intent.putExtra("monitor_position", modelMonitor.getMonitorPosition());
                    intent.putExtra("monitoring_date", modelMonitor.getMonitoringDate());
                    intent.putExtra("monitor_name", modelMonitor.getMonitorName());
                    intent.putExtra("address", modelMonitor.getAddress());
                    startActivity(intent);
                }
            });
        }
    }
}